import React from 'react'
import Link from 'next/link'
import Head from '../components/head'
import Nav from '../components/nav'
import fetch from 'isomorphic-unfetch'


function addUser(){
  console.log(document.forms["registerForm"]["netid"].value);

  userInfo = {
    "name": document.forms["registerForm"]["name"].value, 
    "netid": document.forms["registerForm"]["netid"].value, 
    "campus_organizations": document.forms["registerForm"]["campusorg"].value,
    "hosting_address": document.forms["registerForm"]["address"].value,
    "max_visitors": parseInt(document.forms["registerForm"]["address"].value),
    "gender": document.forms["registerForm"]["gender"].value, 
    "same_gender": true,
    "expandable": true,
    "additional_visitors": 1,
  }
}


const eventList =  (props) => (
  <div>
  <Head title="Events List" />
    <Nav />

    <div className="hero">
      <center> Sign up to host for an event!</center>

      <form id="registerForm">
        Name: <input type="text" name="name" />

        Net ID: <input type="text" name="netid" id="netid" />
        Email: <input type="text" name="email" id="email" />
        Campus Organization: <input type="text" name="campusorg" id="campusorg" />
        Hosting Address: <input type="text" name="address" id="address" />
        Max Visitors: <input type="text" name="maxvisitor" id="maxvisitor" />
        Gender: <input type="text" name="gender" id="gender" />

       <button onClick={addUser}> Submit information </button>

      </form>

    </div>
<style jsx>{`
      .hero {
        width: 100%;
        color: #333;
      }
      .title {
        margin: 0;
        width: 100%;
        padding-top: 80px;
        line-height: 1.15;
        font-size: 48px;
      }
      .title,
      .description {
        text-align: center;
      }
      .row {
        max-width: 880px;
        margin: 80px auto 40px;
        display: flex;
        flex-direction: row;
        justify-content: space-around;
      }
      .card {
        padding: 18px 18px 24px;
        width: 220px;
        text-align: left;
        text-decoration: none;
        color: #434343;
        border: 1px solid #9b9b9b;
      }
      .card:hover {
        border-color: #067df7;
      }
      .card h3 {
        margin: 0;
        color: #067df7;
        font-size: 18px;
      }
      .card p {
        margin: 0;
        padding: 12px 0 0;
        font-size: 13px;
        color: #333;
      }
    `}</style>
  </div>

)

eventList.getInitialProps = async function() {
  const res = await fetch('http://localhost:5000/event/1', {
        method: "GET",
        headers: {
            "Content-Type": "text/plain",
            "Access-Control-Allow-Origin": "*"
        }})
  var data = await res.json()
  data = JSON.stringify(data)

 data = JSON.parse(data)


  return {
    description: data['description'], 
    end_date: data['end_date'],
    end_time: data['end_time'],
    expected_number_visitors: data['expected_number_visitors'],
    hosting_organization: data['hosting_organization'],
    location: data['location'],
    name: data['name'],
    number_of_hosts: data['number_of_hosts'],
    start_date: data['start_date'],
    start_time: data['start_time']
  }
}

export default eventList
