import React from 'react'
import Link from 'next/link'
import Head from '../components/head'
import Nav from '../components/nav'
import fetch from 'isomorphic-unfetch'
import { Button, Modal, ModalHeader, ModalBody, ModalFooter } from 'reactstrap';



class eventList extends React.Component {
  constructor(props, context){
    super(props, context);
    this.state = {
      modal: false
    };
    this.toggle = this.toggle.bind(this);
    this.addEvent = this.addEvent.bind(this);

  }
  addEvent(){

  }
  toggle() {
    this.setState(prevState => ({
      modal: !prevState.modal
    }));
  }
  static async getInitialProps(){
        const res = await fetch('http://localhost:5000/event/1', {
            method: "GET",
            headers: {
                "Content-Type": "text/plain",
                "Access-Control-Allow-Origin": "*"
            }})
      var data = await res.json()
      data = JSON.stringify(data)

     data = JSON.parse(data)


      return {
        description: data['description'], 
        end_date: data['end_date'],
        end_time: data['end_time'],
        expected_number_visitors: data['expected_number_visitors'],
        hosting_organization: data['hosting_organization'],
        location: data['location'],
        name: data['name'],
        number_of_hosts: data['number_of_hosts'],
        start_date: data['start_date'],
        start_time: data['start_time']
      }


  }
  render(){ 
    return(
  <div>
  <Head title="Events List" />
    <Nav />

    <div className="hero">
      <center> <h2> Events </h2> </center>
      <Button color="danger" onClick={this.toggle}> Enlist a new event </Button>

      <Modal isOpen={this.state.modal} toggle={this.toggle} className={this.props.className}>
          <ModalHeader toggle={this.toggle}>Modal title</ModalHeader>
          <ModalBody>
            Lorem ipsum dolor sit amet, consectetur adipisicing elit, sed do eiusmod tempor incididunt ut labore et dolore magna aliqua. Ut enim ad minim veniam, quis nostrud exercitation ullamco laboris nisi ut aliquip ex ea commodo consequat. Duis aute irure dolor in reprehenderit in voluptate velit esse cillum dolore eu fugiat nulla pariatur. Excepteur sint occaecat cupidatat non proident, sunt in culpa qui officia deserunt mollit anim id est laborum.
          </ModalBody>
          <ModalFooter>
            <Button color="primary" onClick={this.addEvent}>Create Event</Button>{' '}
            <Button color="secondary" onClick={this.toggle}>Cancel</Button>
          </ModalFooter>
      </Modal>

      {/*Event Name: {props.name} {'\n'}

      Event Description: {props.description} {'\n'}

      Start Date: {props.start_date} {'\n'}

      Start Time: {props.start_time} */}

    </div>
<style jsx>{`
      .hero {
        width: 100%;
        color: #333;
      }
      .title {
        margin: 0;
        width: 100%;
        padding-top: 80px;
        line-height: 1.15;
        font-size: 48px;
      }
      .title,
      .description {
        text-align: center;
      }
      .row {
        max-width: 880px;
        margin: 80px auto 40px;
        display: flex;
        flex-direction: row;
        justify-content: space-around;
      }
      .card {
        padding: 18px 18px 24px;
        width: 220px;
        text-align: left;
        text-decoration: none;
        color: #434343;
        border: 1px solid #9b9b9b;
      }
      .card:hover {
        border-color: #067df7;
      }
      .card h3 {
        margin: 0;
        color: #067df7;
        font-size: 18px;
      }
      .card p {
        margin: 0;
        padding: 12px 0 0;
        font-size: 13px;
        color: #333;
      }
    `}</style>
  </div>

)}

